import React, {useState, useEffect, useContext, useRef} from 'react'
import {Link, Redirect} from 'react-router-dom'
import useFetch from 'hooks/useFetch'
import useLocalStorage from 'hooks/useLocalStorage'
import {CurrentUserContext} from 'context/currentUser'
import BackendErrorMessages from 'components/backendErrorMessages'

const Authentication = (props) => {
  const isLogin = props.match.path === '/login'
  const pageTitle = isLogin ? 'Sign In' : 'Sign Up'
  const descriptionLink = isLogin ? '/register' : '/login'
  const descriptionText = isLogin ? 'Need an account?' : 'Have an account?'
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [username, setUsername] = useState('')
  const [isSuccessFullSubmit, setIsSuccessFull] = useState(false)
  const apiUrl = isLogin ? '/users/login' : '/users'
  const [{response, isLoading, error}, doFetch] = useFetch(apiUrl)
  const [token, setToken] = useLocalStorage('mcv_token')
  // const [currentUserState, setCurrentUserState] = useContext(CurrentUserContext)
  const [currentUserState, dispatch] = useContext(CurrentUserContext)

  // const emailRef = useRef(null) // emailRef.current.input можно так обрацаться к элементу
  // console.log('currentUserState', currentUserState)

  const handleSubmit = (event) => {
    event.preventDefault()
    const user = isLogin ? {email, password} : {email, password, username}
    doFetch({
      method: 'post',
      data: {
        user,
      },
    })
  }


  useEffect(() => { // для всторонния эффектов например localStorage, title
    if (!response) {
      return
    }
    setToken(response.user.token)
    setIsSuccessFull(true)
    // setCurrentUserState((state) => ({
    //   ...state,
    //   isLoggedIn: true,
    //   isLoading: false,
    //   currentUser: response.user,
    // }))
    // history.push('/') можно так но мы не будем
    dispatch({type: 'SET_AUTHORIZED', payload: response.user })
  }, [response, setToken, dispatch])

  if (isSuccessFullSubmit) {
    return <Redirect to="/"/>
  }

  return (
    <div className="auth-page">
      <div className="container page">
        <div className="row">
          <div className="col-md-6 offset-md-3 col-xs-12">
            <h1 className="text-xs-center">{pageTitle}</h1>
            <p className="text-xs-center">
              <Link to={descriptionLink}>{descriptionText}</Link>
            </p>
            <form onSubmit={handleSubmit}>
              {error && <BackendErrorMessages backendErrors={error.errors}/>}
              <fieldset>
                {!isLogin && (
                  <fieldset className="form-group">
                    <input
                      type="text"
                      className="form-control form-control-lg"
                      placeholder="Username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                    />
                  </fieldset>
                )}
                <fieldset className="form-group">
                  <input
                    type="email"
                    className="form-control form-control-lg"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </fieldset>
                <fieldset className="form-group">
                  <input
                    type="password"
                    className="form-control form-control-lg"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </fieldset>
                <button
                  className="btn btn-lg btn-primary pull-xs-right"
                  type="submit"
                  disabled={isLoading}
                >
                  {pageTitle}
                </button>
              </fieldset>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Authentication
